import React, {useState} from 'react';
import Step from "./Step"
import Form from "./Form"
import Form2 from "./Form2"
import Form3 from "./Form3"

function App() {

    const [Step, setStep] = useState({
        fName: "",
        lName: "",
        eMail: "",
        phone: "",
        gender: "",
        birth: "",
        comments: ""
    })
  
    function handleChange(event) {
      const {value, name} = event.target
      setFirstStep((prevValue) => {
          return {
              ...prevValue,
              [name]: value
          }
      })
    }

    function handleClick(event) {
      event.preventDefault();
      console.log(Step)
    }
   
    const data = [{
        title: "Step 1: Your Details",
        content: <Form handleClick={handleClick} handleChange={handleChange} Step={Step}/>
    },
    {
        title: "Step 2: More Comments",
        content: <Form2  handleClick={handleClick} handleChange={handleChange} Step={Step}/>
    },
    {
        title: "Step 3: Final Comments",
        content: <Form3  handleClick={handleClick} handleChange={handleChange} Step={Step}/>
    }]

    return (
            <div className="container-steps">
                {data.map(({title, content}) => (
                    <Step title={title} content={content} />
                ))}
            </div>
    )
}

export default App;
