import react, {useState} from 'react';
import Step from "./Step"
import Form from "./Form"
import Form2 from "./Form2"
import Form3 from "./Form3"

function App() {

    const [savedData, setSavedData] = useState({})

    const [isActive, setIsActive] = useState(false)

    const [firstStep, setFirstStep] = useState({
        fName: "",
        lName: "",
        eMail: "",
        telephone: "",
        birth: ""
    })
  
    function handleChange(event) {
      const {value, name} = event.target
      setFirstStep((prevValue) => {
          return {
              ...prevValue,
              [name]: value
          }
      })
    }

    function handleClick(event) {
        event.preventDefault();
        setSavedData({
            fName: firstStep.fName,
            lName: firstStep.lName,
            eMail: firstStep.eMail
        })
        console.log(savedData)
        setIsActive(!isActive);
    }

    const [select, setSelect] = useState("Please choose your gender")

    function secondSubmit(event) {
        event.preventDefault();
        setSavedData(prevData => {
            return {
            ...prevData,
            telephone: secondStep.telephone,
            gender: select,
            birth: secondStep.birth
            }
        })
    }

    function handleChangeSelect(event) {
        const selectedGender = event.target.value
        setSelect(selectedGender)
    }
   
    const data = [{
        title: "Step 1: Your Details",
        content: <Form firstStep={firstStep} handleChange={handleChange} handleClick={handleClick} />
    },
    {
        title: "Step 2: More Comments",
        content: <Form2 handleClick={secondSubmit} handleChangeSelect={handleChangeSelect} select={select} />
    },
    {
        title: "Step 3: Final Comments",
        content: <Form3 />
    }]

    return (
        <div>
            <div className="container-steps">
                {data.map(({title, content}) => (
                    <Step title={title} 
                          content={content} />
                ))}
            </div>
            <div>
                <h3>{savedData.fName}</h3>
                <h3>{savedData.lName}</h3>
                <h3>{savedData.eMail}</h3>
            </div>
        </div>
    )
}

export default App;
