import React, {useState} from 'react';
import Step from "./Step"
import Form from "./Form"
import Form2 from "./Form2"
import Form3 from "./Form3"

function App() {

    const [isActive, setIsActive] = useState(false);

    const [firstStep, setFirstStep] = useState({
        fName: "",
        lName: "",
        eMail: "",
        phone: "",
        gender: "",
        birth: "",
        comments: ""
    })
  
    function handleChange(event) {
      const {value, name} = event.target
      setFirstStep((prevValue) => {
          return {
              ...prevValue,
              [name]: value
          }
      })
    }

    function openTab(event) {
        setIsActive(!isActive)
    }

    function handleClick(event) {
      event.preventDefault();
      const stepOne = {
          fName: firstStep.fName,
          lName: firstStep.lName,
          eMail: firstStep.eMail,
          phone: firstStep.phone,
          gender: firstStep.gender,
          birth: firstStep.birth,
          comments: firstStep.comments
      }
      console.log(stepOne)
    }
   
    const data = [{
        title: "Step 1: Your Details",
        content: <Form onClick={openTab} handleChange={handleChange} firstStep={firstStep}/>
    },
    {
        title: "Step 2: More Comments",
        content: <Form2 onClick={openTab} handleChange={handleChange} firstStep={firstStep}/>
    },
    {
        title: "Step 3: Final Comments",
        content: <Form3  handleClick={handleClick} handleChange={handleChange} firstStep={firstStep}/>
    }]

    return (
            <div className="container-steps">
                {data.map(({title, content}) => (
                    <Step onClick={() => setIsActive(!isActive)} isActive={isActive} title={title} content={content} />
                ))}
            </div>
    )
}

export default App;
