import React, { useState } from 'react';
import Step from "./Step"

function App() {
    
    const [isClicked, setIsClicked] = useState(false);
    const [sectionAppear, setSectionAppear] = useState({})
      
    function toggleClick() {
        setIsClicked(!isClicked);
        setSectionAppear(isClicked ? {height: "0", backgroundColor: "white"} : {height: "200px", backgroundColor: "red"});
        console.log("clicked");
    }

    return (
        <div>
            <div>
                <Step isClicked={isClicked} 
                      toggleClick={toggleClick} 
                      title="Step 1: Your Details"
                      sectionAppear={sectionAppear}
                      />
                <Step isClicked={isClicked} 
                      toggleClick={toggleClick} 
                      title="Step 2: More Comments"
                      sectionAppear={sectionAppear}
                      />
                <Step isClicked={isClicked} 
                      toggleClick={toggleClick} 
                      title="Step 3: Final Comments"
                      sectionAppear={sectionAppear}
                      />
            </div>
        </div>
    )
}

export default App;
