import React, {useState} from 'react';

function Form3() {

    const [thirdStep, setThirdStep] = useState({
        comments: "",
    })

    function handleChange(event) {
        const {value, name} = event.target
        setThirdStep((prevValue) => {
            return {
                ...prevValue,
                [name]: value
            }
        })
    }

    function handleClick(event) {
        event.preventDefault();
        const stepThree = {
            comments: thirdStep.comments,
        }
        axios.post("http://localhost:3001/create", stepThree)
    }

    return (
        <div>
            <form>
                <label className="form-label">
                    <div>
                        <h3>Comments:</h3>
                        <textarea required value={thirdStep.comments} onChange={handleChange} name="comments" type="text" />
                    </div>
                    <button onClick={handleClick} type="submit">Next ></button>
                </label>
            </form>
        </div>
    )
}

export default Form3
