import React, { useState } from 'react';

function Form() {

    const [firstStep, setFirstStep] = useState({
          fName: "",
          lName: "",
          eMail: ""
    })
    
    function handleChange(event) {
        const {value, name} = event.target
        setFirstStep((prevValue) => {
            return {
                ...prevValue,
                [name]: value
            }
        })
        console.log(firstStep)
    }

    return (
        <div>
            <form>
                <label className="form-label">
                    <div className="form">
                        <div>
                            <h3>Name</h3>
                            <input required value={firstStep.fName} onChange={handleChange} name="fName" type="text" />
                        </div>
                        <div>
                            <h3>Surname</h3>
                            <input required value={firstStep.lName} onChange={handleChange} name="lName" type="text" />
                        </div>
                        <div>
                            <h3>E-mail Address</h3>
                            <input required value={firstStep.eMail} onChange={handleChange} name="eMail" type="text" />
                        </div>
                    </div>
                    <button type="submit">Next ></button>
                </label>
            </form>
        </div>
    )
}

export default Form
