import react, {useState} from 'react';
import Step from "./Step"
import Form from "./Form"
import Form2 from "./Form2"
import Form3 from "./Form3"

function App() {

    const [savedData, setSavedData] = useState({})

    const [firstStep, setFirstStep] = useState({
        fName: "",
        lName: "",
        eMail: ""
    })
  
    function handleChange(event) {
      const {value, name} = event.target
      setFirstStep((prevValue) => {
          return {
              ...prevValue,
              [name]: value
          }
      })
    }

    function handleClick(event) {
    event.preventDefault();
    setSavedData({
          fName: firstStep.fName,
          lName: firstStep.lName,
          eMail: firstStep.eMail
      })
    }
   
    const data = [{
        title: "Step 1: Your Details",
        content: <Form handleChange={handleChange} handleClick={handleClick} />
    },
    {
        title: "Step 2: More Comments",
        content: <Form2 handleClick={handleClick} />
    },
    {
        title: "Step 3: Final Comments",
        content: <Form3 />
    }]

    return (
            <div className="container-steps">
                {data.map(({title, content}) => (
                    <Step isActive={isActive}
                          title={title} 
                          content={content} />
                ))}
            </div>
    )
}

export default App;
